            var rect = e.target.getBoundingClientRect();
            newCord=rect.x- clickCordX+x
            e.target.style.left= newCord/10 +"px"
            console.log("new:"+newCord,"left:"+e.target.style.left)
            if(newCord<-150){
                console.log("<-150")
            }
            else if(newCord>150){
                console.log(">150")
            }

          //console.log("new:"+newCord)
        }
        const touchStart = e =>{
            clickCordX =e.touches[0].clientX
            //console.log("click:"+clickCordX)
        }
        const touchEnd = e =>{
            var rect = e.target.getBoundingClientRect()
           //var changesX=e.changedTouches[0].clientX-this.state.x
            var x

            // if(changesX<-150&&e.target.getBoundingClientRect().x!=-2220) {
            //     console.log(e.target.getBoundingClientRect().x)
            //     x = e.target.getBoundingClientRect().x- 800-310 +newCord
            //     e.target.style.left = x  +"px"
            //     //console.log(changesX)
            // }
            // else if(changesX>150&&e.target.getBoundingClientRect().x!=0){
            //     x = e.target.getBoundingClientRect().x+ 800+310+newCord
            //     e.target.style.left = x+ e.target.getBoundingClientRect().x+ "px"
            // }
           // console.log(changesX)
        }



        return (
            <div className="testimonialsContent" id="testimonials">
                <div className="block"></div>
                <div onTouchEnd={touchEnd} onTouchStart={touchStart} onTouchMove={slideTestimonials} className="testimonials">
                    <div id="test">
                        <img src="http://localhost/wp/wp-content/uploads/2014/11/imageedit_56_5889917802-150x150.png" alt=""/>
                        <h3>Aenean ac commodo diam. Etiam ut adipiscing mi, quis mattis odio. Donec orci
                            leo, facilisis quis ipsum quis, facilisis rhoncus lorem. Maecenas non rutrum enim.
                            Proin id laoreet neque.</h3>
                    </div>
                    <div>
                        <img src="http://localhost/wp/wp-content/uploads/2014/11/imageedit_62_8368938602-150x150.png" alt=""/>
                        <h3>Aenean ac commodo diam. Etiam ut adipiscing mi, quis mattis odio. Donec orci
                            leo, facilisis quis ipsum quis, facilisis rhoncus lorem. Maecenas non rutrum enim.
                            Proin id laoreet neque.</h3>
                    </div>
                    <div>
                        <img src="http://localhost/wp/wp-content/uploads/2014/11/imageedit_58_3133542507-150x150.png" alt=""/>
                        <h3>Aenean ac commodo diam. Etiam ut adipiscing mi, quis mattis odio. Donec orci
                            leo, facilisis quis ipsum quis, facilisis rhoncus lorem. Maecenas non rutrum enim.
                            Proin id laoreet neque.</h3>
                    </div>
                </div>
                <div className="block"></div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    testimonials: state.testimonials,
    title: state.header
});

const mapDispatchToProps = dispatch => ({
    fetchData: url => dispatch(testimonialsContentData(url))
});
export default connect(mapStateToProps, mapDispatchToProps)(Testimonials)
