import React, {useState} from "react";
import "../styles/navbar.css";
import logo from "../resource/logo.png";
import {Link, animateScroll as scroll} from "react-scroll";
import About from "./about";
//import { Link } from 'react-router-dom';

const Navbar = () => {
    const [navStyle, setNavStyle] = useState()
    const [hr, setHr] = useState()
    window.addEventListener("scroll", () => {
        if (document.getElementsByTagName("html")[0].scrollTop > 76 && navStyle === undefined) {
            setNavStyle({
                position: "fixed",
                zIndex: 999,
                maxWidth: "100%",
                padding: "20px 34px",
                background: "white"
            })
        } else if (document.getElementsByTagName("html")[0].scrollTop < 76 && navStyle != undefined) {
            setNavStyle()
        }
    })
    const selectNavbar=(e)=>{
        console.log(e)
    }


    const changeButton = () => {
        if (!active) {
            setActive("active");
            setMenuPos({bottom: 0})
        } else if (active) {
            setActive("");
            setMenuPos({})
        }
    }
    const [active, setActive] = useState('')
    const [menuPos, setMenuPos] = useState({})

    return (
        <div className="navbar" style={navStyle}>
            <img src={logo} alt="Logo"/>
            <div onClick={changeButton} className="menuButton">
                <div className={`one ${active}`}></div>
                <div className={`two ${active}`}></div>
                <div className={`three ${active}`}></div>
            </div>
            <div className="menu">
                <nav style={menuPos}>
                    <Link
                        activeClass=""
                        to="header"
                        smooth={true}
                        offset={0}
                        duration={500}
                        onClick={(e) => {setHr({
                            left: "11px"
                        })
                        setActive("")
                        setMenuPos({})
                            selectNavbar(e)
                        }}
                    >HOME</Link>

                    <Link
                        activeClass="pasive"
                        to="about"
                        smooth={true}
                        offset={-90}
                        duration={500}
                        onClick={(e) => {setHr({
                            left: "82px",
                            width: "50px"
                        })
                        setActive("")
                        setMenuPos({})
                        }}
                    >ABOUT</Link>
                    <Link
                        activeClass="pasive"
                        to="team"
                        smooth={true}
                        offset={0}
                        duration={500}
                        onClick={(e) => {setHr({
                            left:"158px",
                            width:"42px"
                        })
                        setActive("")
                        setMenuPos({})}}
                    >TEAM</Link>
                    <Link
                        activeClass="pasive"
                        to="services"
                        smooth={true}
                        offset={0}
                        duration={500}
                        onClick={(e) => {setHr({
                            left:"224px",
                            width:"65px"
                        })
                            setActive("")
                            setMenuPos({})}}
                    >SERVICES</Link>
                    <Link
                        activeClass="pasive"
                        to="work"
                        smooth={true}
                        offset={-270}
                        duration={500}
                        onClick={(e) => {setHr({
                            left:"311px"
                        })
                        setActive("")
                        setMenuPos({})}}
                    >WORK</Link>
                    <Link
                        activeClass="pasive"
                        to="clients"
                        smooth={true}
                        offset={0}
                        duration={500}
                        onClick={(e) => {setHr({
                            left:"383px",
                            width:"57px"
                        })
                        setActive("")
                        setMenuPos({})}}
                    >CLIENTS</Link>
                    <Link
                        activeClass="pasive"
                        to="contact"
                        smooth={true}
                        offset={-25}
                        duration={500}
                        onClick={(e) => {setHr({
                            left:"465px",
                            width:"65px"
                        })
                        setActive("")
                        setMenuPos({})}}
                    >CONTACT</Link>
                    <hr style={hr} className="underLine"/>
                </nav>

            </div>
        </div>
    )
}
export default Navbar
