import React from "react";
import "../styles/sass/team.css";
import Twitter from "../resource/twitter.svg";
import GooglePlus from "../resource/gPlus.svg";
import Facebook from "../resource/fcb.svg";


class Team extends React.Component {
    componentDidMount() {
        this.props.fetchData("http://127.0.0.1:8000/team");
    }

    render() {
        let teamList = this.props.team.map((data, index) => {
            return (
                <div className="team" key={index}>
                    <div className="imgContent">
                        <img src={data.image_source} alt="T2"/>
                        <div className="social">
                            <a className="soc" href={data.socialAccounts[0]}><img src={Twitter} alt="Twitter"/></a>
                            <a className="soc" href={data.socialAccounts[1]}><img src={GooglePlus} alt="GooglePlus"/></a>
                            <a className="soc" href={data.socialAccounts[2]}><img src={Facebook} alt="Facebook"/></a>
                        </div>
                    </div>
                    <h3>{data.name}</h3>
                    <h5>{data.position}</h5>
                    <h6>{data.about}</h6>
                </div>
            )
        })
        return (
            <div className="teamContent" id="team">
                <h1>{this.props.title[2]?this.props.title[2].Team.text.title:""}</h1>
                <div className="teamContext">
                    <hr/>
                    <h6>{this.props.title[2]?this.props.title[2].Team.text.content:""}</h6>
                    <hr/>
                </div>
                <div className="teamList">
                    {teamList}
                </div>
            </div>
        )
    }
}

export default Team
