import request from "./api";
const type="WORKS_CONTENT_DATA_SUCCESS";


export function worksContentDataSuccess(works) {
    return {
        type: type,
        payload: works
    }
}
export const getMoreWorks =  (url,start,count) => async dispatch => {
    var formData=new FormData();
    formData.append("Start", start);
    formData.append("Count", count);
    const res = await request.post(url,formData)
    dispatch(worksContentDataSuccess(res.data))
};

