import React,{useState} from 'react';
import Header from './containers/header';
import About from "./containers/about";
import Team from "./containers/team";
import OurServices from "./containers/services";
import OurWorks from "./containers/works";
import Clients from "./containers/clients";
import Contact from "./containers/contact";
import Testimonials from "./containers/testimonials";

const  App =()=> {
const [headerTopState,setHeaderTop]=useState('null')
const [aboutTopState,setAboutTop]=useState('null')
const [teamTopState,setTeamTop]=useState('null')
const [servicesTopState,setServicesTop]=useState('null')
const [worksTopState,setWorksTop]=useState('null')
const [testimonialsTopState,setTestimonialsTop]=useState('null')
const [clientsTopState,setClientsTop]=useState('null')
const [contactTopState,setContactTop]=useState('null')

setTimeout(function updateState(){
    const headerTop=document.getElementById("header").offsetTop;
    const aboutTop=document.getElementById("about").offsetTop;
    const teamTop=document.getElementById("team").offsetTop;
    const servicesTop=document.getElementById("services").offsetTop;
    const worksTop=document.getElementById("works").offsetTop;
    const testimonialsTop=document.getElementById("testimonials").offsetTop;
    const clientsTop=document.getElementById("clients").offsetTop;
    const contactTop=document.getElementById("contact").offsetTop;
    //console.log(aboutTop)

    setHeaderTop(headerTop);
    setAboutTop(aboutTop);
    setTeamTop(teamTop);
    setServicesTop(servicesTop);
    setWorksTop(worksTop);
    setTestimonialsTop(testimonialsTop);
    setClientsTop(clientsTop);
    setContactTop(contactTop);
}, 1000)
    function updateState(){
        const headerTop=document.getElementById("header").offsetTop;
        const aboutTop=document.getElementById("about").offsetTop;
        const teamTop=document.getElementById("team").offsetTop;
        const servicesTop=document.getElementById("services").offsetTop;
        const worksTop=document.getElementById("works").offsetTop;
        const testimonialsTop=document.getElementById("testimonials").offsetTop;
        const clientsTop=document.getElementById("clients").offsetTop;
        const contactTop=document.getElementById("contact").offsetTop;
        //console.log(aboutTop)

        setHeaderTop(headerTop);
        setAboutTop(aboutTop);
        setTeamTop(teamTop);
        setServicesTop(servicesTop);
        setWorksTop(worksTop);
        setTestimonialsTop(testimonialsTop);
        setClientsTop(clientsTop);
        setContactTop(contactTop);
    }
    if(document.getElementById('test'))document.getElementById('test').onclick=()=>{
        setTimeout(()=>{
            console.log(document.getElementById("testimonials").offsetTop)
        },1000)
        updateState()

    }

    return (
        <div>
            <Header headerTop={headerTopState}
                    aboutTop={aboutTopState}
                    teamTop={teamTopState}
                    servicesTop={servicesTopState}
                    worksTop={worksTopState}
                    testimonialsTop={testimonialsTopState}
                    clientsTop={clientsTopState}
                    contactTop={contactTopState}
            />
            <About />
            <Team />
            <OurServices />
            <OurWorks />
            <Testimonials />
            <Clients />
            <Contact />
        </div>
    );
}

export default App;
