import instance from "./api";
import {testimonialsContentDataSuccess} from "./testimonialsAC";

const type="WORKS_CONTENT_DATA_SUCCESS"
export function worksContentDataSuccess(works) {
    return {
        type: type,
        payload: works
    }
}
// export const getMoreWorks =  (url,start,count) => async dispatch => {
//     var formData=new FormData();
//     formData.append("Start", start);
//     formData.append("Count", count);
//     const res = await instance.post(url,formData)
//     dispatch(worksContentDataSuccess(res.data))
// }
export const getMoreWorks=(url,start,count)=> dispatch =>{
    var formData=new FormData();
    formData.append("Start", start);
    formData.append("Count", count);
    fetch(url,{
        method:'post',
        body: formData
    }).then(
        response => {
            if (!response.ok) {
                throw new Error(response.statusText)
            }
            return response
        })
        .then(response => response.json())
        .then(works => dispatch(worksContentDataSuccess(works)))
}
