import React, {useState} from "react";
import "../styles/navbar.css";
import logo from "../resource/logo.png";
import { Link, animateScroll as scroll } from "react-scroll";
import About from "./about";
//import { Link } from 'react-router-dom';

const Navbar = () => {
    const [navStyle,setNavStyle]=useState()
    const [hr,setHr]=useState()
    window.addEventListener("scroll",()=>{
        if(document.getElementsByTagName("html")[0].scrollTop>76 && navStyle===undefined){
            setNavStyle({
                position:"fixed",
                zIndex: 999,
                maxWidth:"100%",
                padding:"20px 34px",
                background:"white"
            })
        }
        else if(document.getElementsByTagName("html")[0].scrollTop<76 && navStyle!=undefined){
         setNavStyle()
        }
    })
    const changeButton = () => {
        if (!active){
            setActive("active");
            setMenuPos({bottom:0})
        }
        else if(active){
            setActive("");
            setMenuPos({})
        }
    }
    const [active,setActive]=useState('')
    const [menuPos,setMenuPos]=useState({})

    return (
        <div className="navbar" style={navStyle}>
            <img src={logo} alt="Logo"/>
            <div onClick={changeButton} className="menuButton">
                <div className={`one ${active}`}></div>
                <div className={`two ${active}`}></div>
                <div className={`three ${active}`}></div>
            </div>
            <div className="menu">
                <nav style={menuPos}>
                    <Link
                        to="home"
                        spy={true}
                        smooth={true}
                        offset={-70}
                        duration= {500}
                        onClick={()=>setHr({

                        })}
                    >HOME</Link>
                    <Link
                        to="about"
                        spy={true}
                        smooth={true}
                        offset={-70}
                        duration= {500}
                        onClick={()=>setHr({

                        })}
                    >ABOUT</Link>
                    <Link
                        to="team"
                        spy={true}
                        smooth={true}
                        offset={-70}
                        duration= {500}
                        onClick={()=>setHr({

                        })}
                    >TEAM</Link>
                    <Link
                        to="services"
                        spy={true}
                        smooth={true}
                        offset={-70}
                        duration= {500}
                        onClick={()=>setHr({

                        })}
                    >SERVICES</Link>
                    <Link
                        to="work"
                        spy={true}
                        smooth={true}
                        offset={-70}
                        duration= {500}
                        onClick={()=>setHr({

                        })}
                    >WORK</Link>
                    <Link
                        to="clients"
                        spy={true}
                        smooth={true}
                        offset={-70}
                        duration= {500}
                        onClick={()=>setHr({

                        })}
                    >CLIENTS</Link>
                    <Link
                        to="contact"
                        spy={true}
                        smooth={true}
                        offset={-70}
                        duration= {500}
                        onClick={()=>setHr({

                        })}
                    >CONTACT</Link>
                    <hr className="underLine"/>
                </nav>

            </div>
        </div>
    )
}
export default Navbar
