import React,{useState} from "react";
import "../styles/testimonials.css";
import {testimonialsContentData} from "../actions/testimonialsAC";
import {connect} from "react-redux"

class Testimonials extends React.Component {
    componentDidMount() {
        this.props.fetchData("http://localhost/backend/team.php");
    }

    render() {
        const [x,setX]= useState()
        const func= e =>{
            var x = e.touches[0].clientX;
            var rect = e.target.getBoundingClientRect()
            console.log(rect.x+e.changedTouches[0].clientX)
        }
        const touchStart = e =>{
            setX(e.touches[0].clientX)
            console.log(x)
        }
        const touchEnd = e =>{
            console.log(e.changedTouches[0].clientX)
        }

        return (
            <div className="testimonialsContent" id="testimonials">
                <div className="block"></div>
                <div onTouchEnd={touchEnd} onTouchStart={touchStart} onTouchMove={func} className="testimonials">
                    <div>
                        <img src="http://localhost/wp/wp-content/uploads/2014/11/imageedit_56_5889917802-150x150.png" alt=""/>
                        <h3>Aenean ac commodo diam. Etiam ut adipiscing mi, quis mattis odio. Donec orci
                            leo, facilisis quis ipsum quis, facilisis rhoncus lorem. Maecenas non rutrum enim.
                            Proin id laoreet neque.</h3>
                    </div>
                    <div>
                        <img src="http://localhost/wp/wp-content/uploads/2014/11/imageedit_62_8368938602-150x150.png" alt=""/>
                        <h3>Aenean ac commodo diam. Etiam ut adipiscing mi, quis mattis odio. Donec orci
                            leo, facilisis quis ipsum quis, facilisis rhoncus lorem. Maecenas non rutrum enim.
                            Proin id laoreet neque.</h3>
                    </div>
                    <div>
                        <img src="http://localhost/wp/wp-content/uploads/2014/11/imageedit_58_3133542507-150x150.png" alt=""/>
                        <h3>Aenean ac commodo diam. Etiam ut adipiscing mi, quis mattis odio. Donec orci
                            leo, facilisis quis ipsum quis, facilisis rhoncus lorem. Maecenas non rutrum enim.
                            Proin id laoreet neque.</h3>
                    </div>
                </div>
                <div className="block"></div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    testimonials: state.testimonials,
    title: state.header
});

const mapDispatchToProps = dispatch => ({
    fetchData: url => dispatch(testimonialsContentData(url))
});
export default connect(mapStateToProps, mapDispatchToProps)(Testimonials)
