import React, {useState} from "react";
import "../styles/navbar.css";
import logo from "../resource/logo.png";
import About from "./about";
//import { Link } from 'react-router-dom';

const Navbar = () => {
    const [navStyle,setNavStyle]=useState()
    window.addEventListener("scroll",()=>{
        if(document.getElementsByTagName("html")[0].scrollTop>76 && navStyle===undefined){
            setNavStyle({
                position:"fixed",
                zIndex: 999,
                maxWidth:"100%",
                padding:"20px 34px",
                background:"white"
            })
        }
        else if(document.getElementsByTagName("html")[0].scrollTop<76 && navStyle!=undefined){
         setNavStyle()
        }
    })
const test=()=>{
    scrollToRef(About)
}

    const changeButton = () => {
        if (!active){
            setActive("active");
            setMenuPos({bottom:0})
        }
        else if(active){
            setActive("");
            setMenuPos({})
        }
    }
    const [active,setActive]=useState('')
    const [menuPos,setMenuPos]=useState({})

    return (
        <div className="navbar" style={navStyle}>
            <img src={logo} alt="Logo"/>
            <div onClick={changeButton} className="menuButton">
                <div className={`one ${active}`}></div>
                <div className={`two ${active}`}></div>
                <div className={`three ${active}`}></div>
            </div>
            <div className="menu">
                <nav style={menuPos}>
                    <button>HOME</button>
                    <button onClick={()=>scrollToRef({About})}>ABOUT</button>
                    <button>TEAM</button>
                    <button>SERVICES</button>
                    <button>WORK</button>
                    <button>CLIENTS</button>
                    <button>CONTACT</button>
                    <hr className="underLine"/>
                </nav>

            </div>
        </div>
    )
}
export default Navbar
