import React from "react";
import '../styles/sass/about.css';
import anchorIcon from "../resource/anchor.png";

import {Link} from "react-scroll";

class About extends React.Component {
    stringToHTML = function (str) {
        var parser = new DOMParser();
        var doc = parser.parseFromString(str, 'text/html');
        console.log(doc.body.getElementsByTagName("*"))
        return doc.body.getElementsByTagName("*");
    };
    render() {

        return (
            <div className="aboutContent" id="about">
                <img src={anchorIcon} alt="Anchor"/>
                <h1>{this.props.header ? this.stringToHTML(this.props.header):''}</h1>
                <Link
                    activeClass=""
                    to="work"
                    smooth={true}
                    offset={-270}
                    duration={500}
                >OUR WORK</Link>
            </div>
        )
    }
}

export default About
