import React from "react";
import "../styles/ourWorks.css";
import {getMoreWorks} from "../actions/worksAC";
import {connect} from "react-redux";
class OurWorks extends React.Component{
    state = {
        start: 4,
        end:4
    };
    componentDidMount() {
        this.props.fetchData("http://localhost/backend/works.php",this.state.start);
    }
    render(){
    var worksArray=[]
        worksArray=[this.props.works]
        console.log(worksArray)
        window.onLoad=()=>{let OurWorks = worksArray.map((data, index) => {
            return (
                <div key={index} className="work">
                    <img src={data.img} alt="img"/>
                    <div className="workInfo">
                        <h4>{data.name}</h4>
                        <h6>{data.context}</h6>
                    </div>
                </div>
            )
        })}
        var start=this.state.start
        var end=this.state.end
        console.log(start)
        const getMoreData=()=>{
            start+=4
            end+=4
            this.setState(start={start})
            this.setState(end={end})
            this.props.fetchData("http://localhost/backend/works.php",this.state.start+4,this.state.end+4);
        }

        return (
            <div className="worksContent">
                <h1>{this.props.title.workTitle ? this.props.title.workTitle.title:""}</h1>
                <div className="worksContext">
                    <hr/>
                    <h6>{this.props.title.workTitle?this.props.title.workTitle['context']:""}</h6>
                    <hr/>
                </div>
                <div className="workList">
                    {OurWorks}
                </div>
                <button onClick={getMoreData}>LOAD MORE</button>
            </div>
        )}
}

const mapStateToProps = state => ({
    works: state.works,
    title: state.header
});
const mapDispatchToProps = dispatch => ({
    fetchData: (url,start,end)=> dispatch(getMoreWorks(url,start,end))
});
export default connect(mapStateToProps, mapDispatchToProps)(OurWorks)
