import React from "react";
import "../styles/testimonials.css";
import {testimonialsContentData} from "../actions/testimonialsAC";
import {connect} from "react-redux"

class Testimonials extends React.Component {
    state = {
        sliderChildWidth: 0
    };

    componentDidMount() {
        this.props.fetchData("http://localhost/backend/testimonials.php");

        window.addEventListener("resize", () => {
            this.sliderSize();
            this.getWindowWidth();
            document.getElementById("slider").style.left = 0
        });
        window.addEventListener("load", () => {
            this.sliderSize();
            this.getWindowWidth();
        });
    }

    getWindowWidth = () => {
        var sliderChildWidth;
        if (document.getElementById("slider").children[0]) {
           // setTimeout("", 200)
            sliderChildWidth = document.getElementById("slider").children[0].clientWidth
        }
        this.setState({sliderChildWidth});
    };

    sliderSize = () => {
        if (window.innerWidth <= 650 && window.innerWidth >= 440) {
            console.log(document.getElementById("slider").children)
            for (let i in document.getElementById("slider").children) {
                if (document.getElementById("slider").children[i].style) {
                    document.getElementById("slider").children[i].style.width = window.innerWidth - 234 + 'px'
                    document.getElementById("slider").children[i].style.padding = "0 117px";
                }
            }
        } else if (window.innerWidth <= 440) {
            for (let i in document.getElementById("slider").children) {
                if (document.getElementById("slider").children[i].style) {
                    document.getElementById("slider").children[i].style.width = window.innerWidth - 134 + 'px'
                    document.getElementById("slider").children[i].style.padding = "0 67px"
                }
            }
        } else {
            for (let i in document.getElementById("slider").children) {
                if (document.getElementById("slider").children[i].style) {
                    document.getElementById("slider").children[i].style.width = window.innerWidth - 310 + 'px'
                    document.getElementById("slider").children[i].style.padding = "0 155px"
                }
            }
        }
    };

    render() {
        if(document.getElementsByClassName("sliderPos")[0])document.getElementsByClassName("sliderPos")[0].innerHTML = '';
        let testimonials = this.props.testimonials.map((data, index) => {
            var div = document.createElement('div');
            div.className = "sld";
            document.getElementsByClassName("sliderPos")[0].appendChild(div);
            return (
                <div className="slides" key={index}>
                    <img draggable="false"
                         src={data.img}
                         alt=""/>
                    <h3>{data.context}</h3>
                </div>
            )
        });
        if(document.getElementsByClassName("slides")[0]){document.getElementsByClassName("slides")[0].addEventListener('load',()=>{
            console.log('dcds')
        })}



        // window.onload = function fun(){
        //     console.log(document.getElementsByClassName('slides').length)
        //     for (var i = 0; i < document.getElementsByClassName('slides').length; i++) {
        //         var div = document.createElement('div')
        //         div.className = "sld"
        //         document.getElementById("sld").append(div)
        //     }
        //
        // }

        var sliderChildWidth = this.state.sliderChildWidth;
        var leftX;
        var clickCordX;
        var newCord;
        var changedX;
        var clicked = false
        var mouseClickX
        const touchStart = e => {
            if (e.clientX) {
                clicked = true
                mouseClickX = e.clientX
            }
            if (document.getElementById("slider")) {
                sliderChildWidth = document.getElementById("slider").children[0].clientWidth;
            }
            var rect = document.getElementById("slider").getBoundingClientRect();
            if (e.touches) clickCordX = e.touches[0].clientX;
            leftX = rect.x;
            changedX = 0
        };
        const slideTestimonials = e => {
            document.getElementById("slider").style.transition = "0s";
            if (e.touches) {
                if (e.touches[0]) var touchX = e.touches[0].clientX;
                changedX = touchX - clickCordX;
                newCord = touchX - clickCordX + leftX;
                if (document.getElementById("slider")) document.getElementById("slider").style.left = newCord + "px";
            }
            if (clicked) {
                document.getElementById("slider").style.cursor = "grab"

                if (e.clientX) touchX = e.clientX;
                changedX = touchX - mouseClickX;
                newCord = touchX - mouseClickX + leftX;

                if (document.getElementById("slider")) document.getElementById("slider").style.left = newCord + "px";
            } else {
                document.getElementById("slider").style.cursor = "default"
            }

        };
        document.getElementsByTagName('img').ondragstart = function () {
            return false;
        };
        var nextPos = -sliderChildWidth;
        var backPos = sliderChildWidth;
        var sliderActived = true;
        var sliderBackActived = true;
        var sliderIcon = 0
        const touchEnd = e => {
            if (document.getElementById("slider")) document.getElementById("slider").style.transition = ".5s";
            var rect = document.getElementById("slider").getBoundingClientRect();
            if (e.clientX) {
                if (mouseClickX - e.clientX > 150) {
                    if (sliderActived) {
                        backPos -= sliderChildWidth;

                        document.getElementById("slider").style.left = nextPos + "px";
                        nextPos = nextPos - sliderChildWidth;

                        sliderBackActived = true
                        document.getElementsByClassName("sld")[sliderIcon].style.background = "white"
                        sliderIcon++
                        if (document.getElementsByClassName("sld")[sliderIcon]) document.getElementsByClassName("sld")[sliderIcon].style.background = "aqua"
                    } else {
                        nextPos = -sliderChildWidth;
                        if (document.getElementById("slider")) document.getElementById("slider").style.left = 0;
                        backPos = sliderChildWidth;
                        sliderActived = true

                        document.getElementsByClassName("sld")[sliderIcon].style.background = "white"
                        sliderIcon = 0
                        document.getElementsByClassName("sld")[sliderIcon].style.background = "aqua"
                    }
                } else if (mouseClickX - e.clientX < -150) {
                    if (sliderBackActived) {
                        if (document.getElementsByClassName("sld")[sliderIcon]) {
                            document.getElementsByClassName("sld")[sliderIcon].style.background = "white"
                            sliderIcon--
                            document.getElementsByClassName("sld")[sliderIcon].style.background = "aqua"
                        }
                        nextPos += sliderChildWidth;
                        if (document.getElementById("slider")) document.getElementById("slider").style.left = backPos + "px";
                        backPos += sliderChildWidth
                    } else {
                        nextPos = -sliderChildWidth;
                        if (document.getElementById("slider")) document.getElementById("slider").style.left = 0;
                        backPos = sliderChildWidth
                    }
                } else {
                    document.getElementById("slider").style.left = rect.x - changedX + "px";
                }
                if (document.getElementById("slider").style.left === -sliderChildWidth * (document.getElementById("slider").childElementCount - 1) + "px") {
                    sliderActived = false
                }
                if (document.getElementById("slider").style.left === "0px") {
                    sliderBackActived = false
                }
                clicked = false
            }

            //////////////////////////////////////////////////////////////////////////////////////////////////////////////

            if (e.touches) {
                if (rect.x > 0) {
                    sliderBackActived = false
                }
                if (changedX < -150) {
                    if (sliderActived) {
                        document.getElementsByClassName("sld")[sliderIcon].style.background = "white"
                        sliderIcon++
                        if (document.getElementsByClassName("sld")[sliderIcon]) document.getElementsByClassName("sld")[sliderIcon].style.background = "aqua"
                        backPos -= sliderChildWidth;
                        if (document.getElementById("slider")) document.getElementById("slider").style.left = nextPos + "px";
                        nextPos += -sliderChildWidth;
                        sliderBackActived = true
                    } else {
                        nextPos = -sliderChildWidth;
                        if (document.getElementById("slider")) document.getElementById("slider").style.left = 0;
                        backPos = sliderChildWidth;
                        sliderActived = true

                        document.getElementsByClassName("sld")[sliderIcon].style.background = "white"
                        sliderIcon = 0
                        document.getElementsByClassName("sld")[sliderIcon].style.background = "aqua"
                    }
                } else if (changedX > 150) {
                    if (sliderBackActived) {
                        nextPos += sliderChildWidth;
                        if (document.getElementById("slider")) document.getElementById("slider").style.left = backPos + "px";
                        backPos += sliderChildWidth
                        if (document.getElementsByClassName("sld")[sliderIcon]) {
                            document.getElementsByClassName("sld")[sliderIcon].style.background = "white"
                            sliderIcon--
                            document.getElementsByClassName("sld")[sliderIcon].style.background = "aqua"
                        }
                    } else {
                        nextPos = -sliderChildWidth;
                        if (document.getElementById("slider")) document.getElementById("slider").style.left = 0;
                        backPos = sliderChildWidth
                    }
                } else {
                    document.getElementById("slider").style.left = rect.x - changedX + "px";
                }
                if (document.getElementById("slider").style.left === -sliderChildWidth * (document.getElementById("slider").childElementCount - 1) + "px") {
                    sliderActived = false
                }
                if (document.getElementById("slider").style.left === "0px") {
                    sliderBackActived = false
                }
            }
            changedX = 0
        };

        const mouseOut = e => {
            var rect = document.getElementById("slider").getBoundingClientRect();
            document.getElementById("slider").style.left = rect.x - changedX + "px"
            clicked = false;
            changedX = 0
        }

        setInterval(() => {
            if (sliderActived&&nextPos!==-0) {
                if (document.getElementById("slider")) document.getElementById("slider").style.transition = ".5s";
                document.getElementsByClassName("sld")[sliderIcon].style.background = "white"
                sliderIcon++
                if (document.getElementsByClassName("sld")[sliderIcon]) document.getElementsByClassName("sld")[sliderIcon].style.background = "aqua"
                backPos -= sliderChildWidth;
                if (document.getElementById("slider")) document.getElementById("slider").style.left = nextPos + "px";
                nextPos += -sliderChildWidth    ;
                sliderBackActived = true
            }
            if(!sliderActived&&nextPos!==-0) {
                nextPos = -sliderChildWidth;
                if (document.getElementById("slider")) document.getElementById("slider").style.left = 0;
                backPos = sliderChildWidth;
                sliderActived = true
                document.getElementsByClassName("sld")[sliderIcon].style.background = "white"
                sliderIcon = 0
                document.getElementsByClassName("sld")[sliderIcon].style.background = "aqua"
            }
            if (document.getElementById("slider").style.left === -sliderChildWidth * (document.getElementById("slider").childElementCount - 1) + "px") {
                sliderActived = false
            }
        }, 15000)
        return (
            <div className="testimonialsContent" id="testimonials">
                <div className="block"></div>
                <div onMouseLeave={mouseOut} onMouseMove={slideTestimonials} onMouseDown={touchStart}
                     onMouseUp={touchEnd} onTouchEnd={touchEnd} onTouchStart={touchStart}
                     onTouchMove={slideTestimonials}
                     className="testimonials" id="slider">
                    {testimonials}
                </div>
                <div className="block"></div>
                <div className="sliderPos" id="sld"></div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    testimonials: state.testimonials,
    title: state.header
});

const mapDispatchToProps = dispatch => ({
    fetchData: url => dispatch(testimonialsContentData(url))
});
export default connect(mapStateToProps, mapDispatchToProps)(Testimonials)
