import React from "react";
import "../styles/ourWorks.css";
import {getMoreWorks} from "../actions/worksAC";
import {connect} from "react-redux";
class OurWorks extends React.Component{
    state = {
        start: 4
    };
    componentDidMount() {
        this.props.fetchData("http://localhost/backend/works.php",this.state.start);
    }
    render(){

        let OurWorks = this.props.works.map((data, index) => {
            return (
                <div key={index} className="work">
                    <img src={data.img} alt="img"/>
                    <div className="workInfo">
                        <h4>{data.name}</h4>
                        <h6>{data.context}</h6>
                    </div>
                </div>
            )
        })
        var start=this.state.start
        console.log(start)
        // const getMoreData=()=>{
        //     start+=4
        //     this.setState({start})
        //     this.props.fetchData("http://localhost/backend/works.php",this.state.start+4);
        //
        // }

        return (
            <div className="worksContent">
                <h1>{this.props.title.workTitle ? this.props.title.workTitle.title:""}</h1>
                <div className="worksContext">
                    <hr/>
                    <h6>{this.props.title.workTitle?this.props.title.workTitle['context']:""}</h6>
                    <hr/>
                </div>
                <div className="workList">
                    {OurWorks}
                </div>
                <button onClick={getMoreData}>LOAD MORE</button>
            </div>
        )}
}

const mapStateToProps = state => ({
    works: state.works,
    title: state.header
});
const mapDispatchToProps = dispatch => ({
    fetchData: (url,start)=> dispatch(getMoreWorks(url,start))
});
export default connect(mapStateToProps, mapDispatchToProps)(OurWorks)
