import React from "react";
import "../styles/sass/ourWorks.css";
import {getMoreWorks} from "../actions/worksAC";
import {connect} from "react-redux";

class OurWorks extends React.Component {
    state = {
        start: 4,
        loader:false,
        workList: []
    };

    componentDidMount() {
        this.props.fetchData("works.php", this.state.start-4, 4);
    }


    render() {
        const {works} = this.props;
        var count = 4;
        var start = this.state.start;

        const getMoreData = () => {
            this.setState({loader:true})

            this.props.fetchData("works.php", this.state.start, count);
            start+=4
            this.setState(start = {start});
            this.setState({loader:false})
        }


        // console.log(this.state.start)
        if(this.state.loader){
            console.log("loading...")
        }
            return (
                <div className="worksContent">
                    <h1>{this.props.title.workTitle ? this.props.title.workTitle.title : ""}</h1>
                    <div className="worksContext">
                        <hr/>
                        <h6>{this.props.title.workTitle ? this.props.title.workTitle.context : ""}</h6>
                        <hr/>
                    </div>
                    <div className="workList">
                        {works.map((data, index) => {
                            return (
                                <div key={index} className="work">
                                    <img src={data.img} alt="img"/>
                                    <div className="workInfo">
                                        <h4>{data.name}</h4>
                                        <h6>{data.context}</h6>
                                    </div>
                                </div>
                            )
                        })}
                    </div>
                    <button onClick={getMoreData}>LOAD MORE</button>
                </div>
            )
        }
    }


const mapStateToProps = state => ({
    works: state.works,
    title: state.header
});
const mapDispatchToProps = dispatch => ({
    fetchData: (url, start, end) => dispatch(getMoreWorks(url, start, end))
});
export default connect(mapStateToProps, mapDispatchToProps)(OurWorks)
