import React,{useState} from "react";
import "../styles/testimonials.css";
import {testimonialsContentData} from "../actions/testimonialsAC";
import {connect} from "react-redux"

class Testimonials extends React.Component {
    componentDidMount() {
        this.props.fetchData("http://localhost/backend/team.php");
        for(let i in document.getElementById("slider").children) {
            if(document.getElementById("slider").children[i].style) {
                document.getElementById("slider").children[i].style.width = window.innerWidth - 310 + 'px'
            }
        }
    }

    render() {
        window.addEventListener("resize",()=>{
if(window.innerWidth<=680){
            for(let i in document.getElementById("slider").children) {
                if(document.getElementById("slider").children[i].style) {
                    document.getElementById("slider").children[i].style.width = window.innerWidth - 310 + 'px'
                    document.getElementById("slider").children[i].style.margin="100px 117px"
                }
            }}
            if(window.innerWidth<=440){
                for(let i in document.getElementById("slider").children) {
                    if(document.getElementById("slider").children[i].style) {
                        document.getElementById("slider").children[i].style.width = window.innerWidth - 134 + 'px'
                        document.getElementById("slider").children[i].style.margin="100px 67px"
                    }
                }}
            else{
                for(let i in document.getElementById("slider").children) {
                    if(document.getElementById("slider").children[i].style) {
                        document.getElementById("slider").children[i].style.width = window.innerWidth - 310 + 'px'
                    }
                }}
        })
        var sliderChildWidth
        if(document.getElementById("slider")){
            sliderChildWidth=document.getElementById("slider").children[0].clientWidth+310
        }
        var leftX
        var clickCordX
        var newCord
        var changedX
        const slideTestimonials= e =>{
            e.target.style.transition="0s"
            var touchX = e.touches[0].clientX;
            changedX=touchX-clickCordX
            newCord= touchX-clickCordX + leftX;
            e.target.style.left= newCord +"px";
        }
        const touchStart = e =>{
            var rect = e.target.getBoundingClientRect()
            clickCordX =e.touches[0].clientX
            leftX = rect.x
            changedX=0
            console.log(e.target.children[0].clientWidth)
        }
        var nextPos=-sliderChildWidth
        var backPos=sliderChildWidth
        var sliderActived = true
        var sliderBackActived = true
        const touchEnd = e =>{
            e.target.style.transition=".5s"
            var rect = e.target.getBoundingClientRect()
            if(rect.x>0){
                sliderBackActived=false
            }
            if(changedX<-150){
                if(sliderActived) {
                    backPos -= sliderChildWidth
                    e.target.style.left = nextPos + "px";
                    nextPos += -sliderChildWidth
                    sliderBackActived=true
                }
                else{
                    nextPos=-sliderChildWidth
                    e.target.style.left = 0;
                    backPos=sliderChildWidth
                    sliderActived=true
                }
            }
            else if(changedX>150){
                if(sliderBackActived) {
                    nextPos += sliderChildWidth
                    e.target.style.left = backPos + "px";
                    backPos += sliderChildWidth
                }
                else{
                    nextPos=-sliderChildWidth
                    e.target.style.left = 0;
                    backPos=sliderChildWidth
                }
            }
            else{
                e.target.style.left= rect.x - changedX +"px";
            }
            if(e.target.style.left===-sliderChildWidth*(e.target.childElementCount-1)+"px") {
                sliderActived=false
            }
            if(e.target.style.left==="0px"){
                sliderBackActived=false
            }
        }



        return (
            <div className="testimonialsContent" id="testimonials">
                <div className="block"></div>
                <div onTouchEnd={touchEnd} onTouchStart={touchStart} onTouchMove={slideTestimonials} className="testimonials" id="slider">
                    <div id="test">
                        <img src="http://localhost/wp/wp-content/uploads/2014/11/imageedit_56_5889917802-150x150.png" alt=""/>
                        <h3>Aenean ac commodo diam. Etiam ut adipiscing mi, quis mattis odio. Donec orci
                            leo, facilisis quis ipsum quis, facilisis rhoncus lorem. Maecenas non rutrum enim.
                            Proin id laoreet neque.</h3>
                    </div>
                    <div>
                        <img src="http://localhost/wp/wp-content/uploads/2014/11/imageedit_62_8368938602-150x150.png" alt=""/>
                        <h3>Aenean ac commodo diam. Etiam ut adipiscing mi, quis mattis odio. Donec orci
                            leo, facilisis quis ipsum quis, facilisis rhoncus lorem. Maecenas non rutrum enim.
                            Proin id laoreet neque.</h3>
                    </div>
                    <div>
                        <img src="http://localhost/wp/wp-content/uploads/2014/11/imageedit_58_3133542507-150x150.png" alt=""/>
                        <h3>Aenean ac commodo diam. Etiam ut adipiscing mi, quis mattis odio. Donec orci
                            leo, facilisis quis ipsum quis, facilisis rhoncus lorem. Maecenas non rutrum enim.
                            Proin id laoreet neque.</h3>
                    </div>
                    <div>
                        <img src="http://localhost/wp/wp-content/uploads/2014/11/imageedit_58_3133542507-150x150.png" alt=""/>
                        <h3>Aenean ac commodo diam. Etiam ut adipiscing mi, quis mattis odio. Donec orci
                            leo, facilisis quis ipsum quis, facilisis rhoncus lorem. Maecenas non rutrum enim.
                            Proin id laoreet neque.</h3>
                    </div>
                </div>
                <div className="block"></div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    testimonials: state.testimonials,
    title: state.header
});

const mapDispatchToProps = dispatch => ({
    fetchData: url => dispatch(testimonialsContentData(url))
});
export default connect(mapStateToProps, mapDispatchToProps)(Testimonials)
