import React, {useState, useEffect} from "react";
import "../styles/sass/navbar.css";
import logo from "../resource/logo.png";
import {Link} from "react-scroll";


const Navbar = (props) => {
    const [navStyle, setNavStyle] = useState()
    const [hr, setHr] = useState()
    window.addEventListener("scroll", () => {
        if (document.getElementsByTagName("html")[0].scrollTop > 76 && navStyle === undefined) {
            setNavStyle({
                position: "fixed",
                zIndex: 999,
                maxWidth: "100%",
                padding: "20px 34px",
                background: "white"
            })
        } else if (document.getElementsByTagName("html")[0].scrollTop < 76 && navStyle !== undefined) {
            setNavStyle()
        }
    })
    const changeButton = () => {
        if (!active) {
            setNavPos({top:"0"})
            setActive("active");
            setMenuPos({
                top:"89px"
            })
            setNavStyle({
                position: "fixed",
                zIndex: 999,
                maxWidth: "100%",
                padding: "20px 34px",
                background: "white"
            })
        } else if (active) {
            setNavPos({top:"-90px"})
            setActive("");
            setMenuPos({})
        }
    }
    const [active, setActive] = useState('')
    const [menuPos, setMenuPos] = useState({})
    const [navPos, setNavPos] = useState({})
    //console.log(props.aboutTop)

            useEffect(
                window.onscroll=()=>{
                    if(document.getElementsByTagName("html")[0].scrollTop>props.headerTop && document.getElementsByTagName("html")[0].scrollTop+120 < props.aboutTop){
                        setHr({
                            left: "11px"
                        })
                        console.log(props.teamTop)
                    }
                    else if(document.getElementsByTagName("html")[0].scrollTop+299>props.aboutTop && document.getElementsByTagName("html")[0].scrollTop+120 < props.teamTop){
                        setHr({
                            left: "82px",
                            width: "50px"
                        })
                        console.log(props.teamTop)
                    }

                }

            , []);

    return (
        <div className="navbar" style={navStyle}>
            <img src={logo} alt="Logo"/>
            <div onClick={changeButton} className="menuButton">
                <div className={`one ${active}`}></div>
                <div className={`two ${active}`}></div>
                <div className={`three ${active}`}></div>
            </div>
            <div style={menuPos} className="menu">
                <nav style={navPos}>
                    <Link
                        className="menuItem"
                        activeClass=""
                        to="header"
                        smooth={true}
                        offset={0}
                        duration={500}
                        onClick={(e) => {
                            setHr({
                                left: "11px"
                            })
                            setActive("")
                            setMenuPos({})
                            setNavPos({top:"-90px"})
                        }}
                    >HOME</Link>

                    <Link
                        className="menuItem"
                        activeClass="pasive"
                        to="about"
                        smooth={true}
                        offset={-90}
                        duration={500}
                        onClick={(e) => {
                            setHr({
                                left: "82px",
                                width: "50px"
                            })
                            setActive("")
                            setMenuPos({})
                            setNavPos({top:"-90px"})
                        }}
                    >ABOUT</Link>
                    <Link
                        className="menuItem"
                        activeClass="pasive"
                        to="team"
                        smooth={true}
                        offset={0}
                        duration={500}
                        onClick={(e) => {
                            setHr({
                                left: "158px",
                                width: "42px"
                            })
                            setActive("")
                            setMenuPos({})
                            setNavPos({top:"-90px"})
                        }}
                    >TEAM</Link>
                    <Link
                        activeClass="pasive"
                        to="services"
                        smooth={true}
                        offset={0}
                        duration={500}
                        onClick={(e) => {
                            setHr({
                                left: "224px",
                                width: "65px"
                            })
                            setActive("")
                            setMenuPos({})
                            setNavPos({top:"-90px"})
                        }}
                    >SERVICES</Link>
                    <Link
                        activeClass="pasive"
                        to="work"
                        smooth={true}
                        offset={-270}
                        duration={500}
                        onClick={(e) => {
                            setHr({
                                left: "311px"
                            })
                            setActive("")
                            setMenuPos({})
                            setNavPos({top:"-90px"})
                        }}
                    >WORK</Link>
                    <Link
                        activeClass="pasive"
                        to="testimonials"
                        smooth={true}
                        offset={-155}
                        duration={500}
                        onClick={(e) => {
                            setHr({
                                left: "383px",
                                width: "95px"
                            })
                            setActive("")
                            setMenuPos({})
                            setNavPos({top:"-90px"})
                        }}
                    >TESTIMONIALS</Link>
                    <Link
                        activeClass="pasive"
                        to="clients"
                        smooth={true}
                        offset={0}
                        duration={500}
                        onClick={(e) => {
                            setHr({
                                left: "505px",
                                width: "57px"
                            })
                            setActive("")
                            setMenuPos({})
                            setNavPos({top:"-90px"})
                        }}
                    >CLIENTS</Link>
                    <Link
                        activeClass="pasive"
                        to="contact"
                        smooth={true}
                        offset={-25}
                        duration={500}
                        onClick={(e) => {
                            setHr({
                                left: "587px",
                                width: "65px"
                            })
                            setActive("")
                            setMenuPos({})
                            setNavPos({top:"-90px"})
                        }}
                    >CONTACT</Link>
                    <hr style={hr} className="underLine"/>
                </nav>
            </div>
        </div>
    )
}
export default Navbar
