import React from "react";
import "../styles/ourWorks.css";
import {getMoreWorks} from "../actions/worksAC";
import {connect} from "react-redux";
class OurWorks extends React.Component{
    state = {
        start: 0,
        count: 4,
        workList: []
    };
    componentDidMount() {
        this.props.fetchData("http://localhost/backend/works.php",this.state.start,this.state.count);
    }


    render(){

        var workList=[]
        if(this.state.workList===[])this.state.workList=this.props.works
        workList=this.state.workList
        let OurWorks = workList.map((data, index) => {
            return (
                <div key={index} className="work">
                    <img src={data.img} alt="img"/>
                    <div className="workInfo">
                        <h4>{data.name}</h4>
                        <h6>{data.context}</h6>
                    </div>
                </div>
            )
        })
        var start=this.state.start
        const getMoreData=()=>{
            start+=4
            this.setState(start={start})
            this.props.fetchData("http://localhost/backend/works.php",this.state.start+4,this.state.count);
            for(var i in this.props.works) {
                this.state.workList = [...this.state.workList, this.props.works[i]]
            }
            OurWorks = workList.map((data, index) => {
                return (
                    <div key={index} className="work">
                        <img src={data.img} alt="img"/>
                        <div className="workInfo">
                            <h4>{data.name}</h4>
                            <h6>{data.context}</h6>
                        </div>
                    </div>
                )
            })
            //console.log(workList)
        }

        console.log(workList)
        return (
            <div className="worksContent">
                <h1>{this.props.title.workTitle ? this.props.title.workTitle.title:""}</h1>
                <div className="worksContext">
                    <hr/>
                    <h6>{this.props.title.workTitle?this.props.title.workTitle['context']:""}</h6>
                    <hr/>
                </div>
                <div className="workList">
                    {OurWorks}
                </div>
                <button onClick={getMoreData}>LOAD MORE</button>
            </div>
        )}
}

const mapStateToProps = state => ({
    works: state.works,
    title: state.header
});
const mapDispatchToProps = dispatch => ({
    fetchData: (url,start,end)=> dispatch(getMoreWorks(url,start,end))
});
export default connect(mapStateToProps, mapDispatchToProps)(OurWorks)
