import React from "react";
import "../styles/ourServices.css";
import Desktop from "../resource/desktop.svg";
import Basket from "../resource/basket.svg";
import Graph from "../resource/graph.svg";
import Lightbulb from "../resource/lightbulb.svg";
import Pen from "../resource/pen.svg";
import Strategy from "../resource/strategy.svg";
import {servicesContentData} from "../actions/servicesAC";
import {connect} from "react-redux"
import {teamContentData} from "../actions/teamAC";
import Twitter from "../resource/twitter.svg";
import GooglePlus from "../resource/gPlus.svg";
import Facebook from "../resource/fcb.svg";

class OurServices extends React.Component{
    componentDidMount() {
        this.props.fetchData("http://localhost/backend/services.php");
    }
    render() {
        let OurServices = this.props.services.map((data, index) => {
            return (
                <div>
                    <img src={data.icon} alt="img"/>
                    <h3>{data.name}</h3>
                    <h6>{data.context}</h6>
                </div>
            )
        })
        return (
            <div className="servicesContent" id="services">
                <h1>{this.props.title.servicTitle?this.props.title.servicTitle['title']:""}</h1>
                <div className="servicesContext">
                    <hr/>
                    <h6>{this.props.title.servicTitle?this.props.title.servicTitle['context']:""}</h6>
                    <hr/>
                </div>
                <div className="serviceList">
                    {OurServices}
                </div>
            </div>
        )
    }
}
const mapStateToProps = state => ({
    services: state.services,
    title: state.header
});

const mapDispatchToProps = dispatch => ({
    fetchData: url => dispatch(servicesContentData(url))
});
export default connect(mapStateToProps, mapDispatchToProps)(OurServices)
