import React, {useState} from "react";
import "../styles/team.css";
import T1 from "../resource/t1.jpg"
import T_2 from "../resource/t2.jpg"
import T_3 from "../resource/t3.jpg"
import T_4 from "../resource/t4.jpg"
import Twitter from "../resource/twitter.svg"
import GooglePlus from "../resource/gPlus.svg"
import Facebook from "../resource/fcb.svg"
import {teamContentData} from "../actions/teamAC";
import {connect} from "react-redux"

class Team extends React.Component {
    componentDidMount() {
        this.props.fetchData("http://localhost/backend/team.php");
    }

    render() {
        let OurTeam = this.props.team.map((data, index) => {
            return (
                <div className="team" key={index}>
                    <div className="imgContent">
                        <img src={data.img} alt="T2"/>
                        <div className="social">
                            <a className="soc" href={data['social']['twitter']}><img src={Twitter} alt="Twitter"/></a>
                            <a className="soc" href={data['social']['gPlus']}><img src={GooglePlus} alt="GooglePlus"/></a>
                            <a className="soc" href={data['social']['fb']}><img src={Facebook} alt="Facebook"/></a>
                        </div>
                    </div>
                    <h3>{data.name}</h3>
                    <h5>{data.pos}</h5>
                    <h6>{data.about}</h6>
                </div>
            )
        })
        return (
            <div className="teamContent" id="team">
                <h1>{this.props.team["title"]}</h1>
                <div className="teamContext">
                    <hr/>
                    <h6>{this.props.team["context"]}</h6>
                    <hr/>
                </div>
                <div className="teamList">
                    {OurTeam}
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    team: state.team
});

const mapDispatchToProps = dispatch => ({
    fetchData: url => dispatch(teamContentData(url))
});
export default connect(mapStateToProps, mapDispatchToProps)(Team)
